mapper.select:
select count(*)
from  live_program
where status=1 and is_del=0;

mapper.update:
update live_program
title=#{title}, begin_time=#{beginTime}, end_time=#{endTime}, details=#{details}, speaker_name=#{speakerName}, type=#{type}, create_time=#{createTime},
where live_id=#{liveId}

update live_program
push_client_ip=#{live.pushClientIp}, video_frame_rate=#{live.videoFrameRate}, bit_rate=#{live.bitRate}, bandwidth=#{live.bandWidth}, viewers_number=#{live.viewersNumber}, traffic_value=#{live.trafficValue},
where live_id=#{live.liveId}

update live_program
actual_begin_time=#{actualBeginTime}, actual_end_time=#{actualEndTime}, is_del=#{isDel}, status=#{status}, pic_count =pic_count+#{picCount}, push_client_ip=#{pushClientIp}, video_frame_rate=#{videoFrameRate}, bit_rate=#{bitRate}, bandwidth=#{bandWidth}, viewers_number=#{number}, traffic_value=#{trafficValue},
where stream_code=#{streamCode}

update live_program set is_del=1,status=4
where live_id in

    #{item.liveId}
